window.onbeforeunload = function () {
  window.scrollTo(0, 0);
}

document.addEventListener("DOMContentLoaded", function(event) {
  var s = skrollr.init();
  console.log("Skrollr loaded");

  skrollr.menu.init(s, {
      complexLinks: false,
      updateUrl: false
  });
  console.log("Skrollr menu loaded");
});
